const { ArgumentTooSmallError } = require("./Errors/ArgumentTooSmallError");
const { ArgumentIsNotANumberError } = require("./Errors/ArgumentIsNotANumberError");
const { NoArgumentError } = require("./Errors/NoArgumentError");

function verifArray(x) {
    if(x.length <= 1) throw new ArgumentTooSmallError("Argument must be more than one.");

    x.filter(function(e) {
        if(typeof e !== "number") throw new ArgumentIsNotANumberError(`Argument must be a number not ${typeof e}`);  
    });
};

function verif(x) {
    if(x === undefined) throw new NoArgumentError("Please put an argument !");
    if(typeof x !== "number") throw new ArgumentIsNotANumberError(`Argument must be a number not ${typeof x}`);
};

function verifMaxMin(arr) {
    if(!Array.isArray(arr)) throw new Error("Argument must be an array.");
    if(arr.length < 1) throw new NoArgumentError("Please put an argument.");
    else if(arr.length == 1) throw new ArgumentTooSmallError("Contain of array must be more than one.");

    for(const e of arr) if(typeof e !== "number") throw new ArgumentIsNotANumberError("Contain of the array must be numbers.");
};

module.exports = {
    verifArray: verifArray, 
    verif: verif,
    verifMaxMin: verifMaxMin
};